    mutations: {
        // SET_USERS_FROM_FIREBASE: (state, response) => {
        //     state.users = response
        // }, 
    }, 
    actions: {
        GET_HOURS ({ commit }) {
            const hours = [];
            const currentHour = Number(moment().format('HH'));
            for (let i = 0; i < 24; i++) {
            let hour = currentHour + i;
            if (hour > 23) {
                hour -= 24; 
            }
            hours.push(`${hour}:00`);
            }
            commit('UPDATE_HOURS', hours);
        },
        
        showWeek({ commit }, currentDate) {
            const days = [];
            const weekStart = currentDate.clone().startOf("week");
            // Остальная логика метода
            commit('updateDays', days);
        },

        // Остальные действия для других методов

    }
}
