export default {
  // Функция для вычисления фона задачи
    calculateTaskColor(task) {
        if (task.type == "EveryDay") {
            return "#C4D7DA";
        }
        if (task.type == "Working") {
            return "#FFDEBF";
        }
        if (task.type == "Common") {
            return "#DDC9C3";
        }
    }, 
}
