// import axios from 'axios'
import moment from 'moment'
moment.locale('ru')

export default {
    state() {
        return {
            hours: [],
            days: [],
        }
        
    }, 
    getters: {
        CURRENT_HOURS: state => {
            return state.tasks
        }
    }, 
    mutations: {
        UPDATE_HOURS: (state, response) => {
            state.hours = response
        }, 
    }, 
    actions: {
        GET_HOURS ({ commit }) {
            const hours = [];
            const currentHour = Number(moment().format('HH'));
            for (let i = 0; i < 24; i++) {
            let hour = currentHour + i;
            if (hour > 23) {
                hour -= 24; 
            }
            hours.push(`${hour}:00`);
            }
            commit('UPDATE_HOURS', hours);
        },
        

    }
}
