// import axios from 'axios'
export default {
  state() {
    return {
      lengthInPixels: null,
    };
  },
  getters: {},
  mutations: {
    SET_TASK_LENGTH_IN_PIXELS: (state, response) => {
      state.heightInPixels = response;
    },
  },
  actions: {
    CALCULATE_TASK_LENGTH_IN_PIXELS({ commit }, task) {
      const [startTime, endTime] = task.time.split(" - ");

      // Разбиваем время начала и времени окончания на часы и минуты
      const [startHour, startMinute] = startTime.split(":").map(Number);
      const [endHour, endMinute] = endTime.split(":").map(Number);

      // Вычисляем продолжительность задачи в минутах
      const durationInMinutes =
        (endHour - startHour) * 60 + (endMinute - startMinute);

      // Вычисляем длину задачи в пикселях (1 час = 80 пикселей)
      const heightInPixels = (durationInMinutes / 60) * 80;

      // Добавляем 10px за каждый пройденный час
      if (endHour - startHour > 1) {
        const addedHours = endHour - startHour;
        let additionalPixels = addedHours * 6;
        return heightInPixels + additionalPixels;
      }

    commit('SET_TASK_LENGTH_IN_PIXELS', heightInPixels)
    },
  },
};
